simple-rule.predicates:
/* Direct access to a non-public attribute of another object */
direct_access_to_attribute(ID):- field_access(ID, _, expression(NOT_THIS), field(FIELD), _, _, compilation_unit(UNIT)), not(this_expression(NOT_THIS, _, _, _, _, _)), field_declaration(FIELD, _, _, _, _, _, _, _), not(public_field(FIELD)), model_unit(ID).
direct_access_to_attribute(ID):- qualified_name(ID, _, _, name(FIELD), _, _, compilation_unit(UNIT)), field_declaration(FIELD, _, _, _, _, _, _, _), not(public_field(FIELD)), model_unit(ID).

/* Abstract class without abstract methods */
/* An abstract class should have at least one abstract method, it is weird to have an abstract class without abstract methods */

abstract_class_without_abstract_method(ID):-
    class_declaration(ID, _, _, _, _, super_type(ST), implements(IMPLEMENTS), _, methods(METHODS), _, compilation_unit(UNIT)),
    abstract_class(ID),
    not(length(METHODS,0)),
    length(IMPLEMENTS,0),
    not(
        (
            ST \= null,
            (
                abstract_class(ST);
                not(class_declaration(ST, _, _, _, _, _, _, _, _, _, _))
            )
        )
    ),
    not(
        (
            method_declaration(METHOD, parent(ID), _, _, _, _, _, _, _, _, _),
            abstract_method(METHOD)
        )
    ),
    model_unit(UNIT).

/* Class or interface that does not define any method more than constructors */

absence_of_methods(ID):-
    ( 
        class_declaration(ID, _, _, _, _, super_type(ST), implements(IMPLEMENTS), _, methods(METHODS), _, compilation_unit(UNIT));
        interface_declaration(ID, _, _, _, _, _, _, _, methods(METHODS), _, compilation_unit(UNIT))
    ),
    (
        length(METHODS,0);
        (
            not(length(METHODS,0)),
            filter_methods(METHODS,AUX),
            length(AUX,0),
            not(
                (
                    abstract_class(ID),
                    ST \= null
                )
            )
        )
    ),
    model_unit(UNIT).

/* Interface implemented by no one */
/* An interface implemented by no one has no sense */

unimplemented_interface(ID):-
    interface_declaration(ID, _, _, _, _, _, _, _, _, _, _),
    not(
        (
            (
                interface_declaration(C1, _, _, _, _, _, implements(IMPLEMENTS), _, _, _, _);
                class_declaration(C1, _, _, _, _, _, implements(IMPLEMENTS), _, _, _, _)
            ),
            member(ID,IMPLEMENTS)
        )
    ).

/* Abstract class extended by no one */
/* An abstract class extended by no one has no sense */

non_extended_abstract_class(ID):-
    abstract_class(ID),
    not(class_declaration(C1, _, _, _, _, super_type(ID), _, _, _, _, _)).

/* Interface implemented just by one class */
/* If the interface is implemented just once, it may be unnecessary */

unnecessary_interface(ID):-
    interface_declaration(ID, _, _, _, _, _, _, _, _, _, _),
    (
        interface_declaration(C1, _, _, _, _, _, implements(IMPLEMENTS), _, _, _, _);
        class_declaration(C1, _, _, _, _, _, implements(IMPLEMENTS), _, _, _, _)
    ),
    member(ID,IMPLEMENTS),
    not(
        (
            (
                interface_declaration(C2, _, _, _, _, _, implements(IMPLEMENTS_2), _, _, _, _);
                class_declaration(C2, _, _, _, _, _, implements(IMPLEMENTS_2), _, _, _, _)
            ),
            C2 \= C1,
            member(ID,IMPLEMENTS_2)
        )
    ).

/* Abstract class extended just by one class */
/* If the abstract class is extended just once, it may be unnecessary */

unnecessary_abstract_class(ID):-
    abstract_class(ID),
    class_declaration(C1, _, _, _, _, super_type(ID), _, _, _, _, _),
    not(
        (
            class_declaration(C2, _, _, _, _, super_type(ID), _, _, _, _, _),
            C1 \= C2
        )
    ).

/* Non abstracted attribute */ /* Two (or more) sister classes have the same attribute (same name and same type). Maybe, the attribute should be abstracted */
non_abstracted_attribute(ID):- class_declaration(CLASS, _, _, _, _, super_type(SUPER_CLASS), _, _, _, _, compilation_unit(UNIT)), SUPER_CLASS \= null, model_unit(UNIT), field_declaration(FIELD, parent(CLASS), _, _, _, _, _, _), not(non_abstracted_attribute_checked(FIELD)), assert(non_abstracted_attribute_checked(FIELD)), not(redefined_attribute(FIELD)), findall(SIMILAR,non_abstracted_attribute_aux(FIELD,SIMILAR),LIST), not(length(LIST,0)), append([FIELD],LIST,ID).
non_abstracted_attribute_aux(FIELD,SIMILAR):- field_declaration(FIELD, parent(CLASS), NAME, type(TYPE), _, extra_dimensions(D), _, _), field_declaration(SIMILAR, parent(CLASS_2), NAME, type(TYPE), _, extra_dimensions(D), _, _), not(non_abstracted_attribute_checked(SIMILAR)), common_super(CLASS,CLASS_2,CLASS_3), CLASS_3 \= CLASS, CLASS_3 \= CLASS_2, CLASS \= CLASS_2, ( class_declaration(CLASS_3, _, _, _, _, _, _, _, _, _, _); interface_declaration(CLASS_3, _, _, _, _, _, _, _, _, _, _) ), assert(non_abstracted_attribute_checked(SIMILAR)).

/* Non abstracted method */ /* Two (or more) sister classes defined a similar method (same name and same parameters). Maybe, the method should be abstracted */
non_abstracted_method(ID):- class_declaration(CLASS, _, _, _, _, super_type(SUPER_CLASS), implements(IMPLEMENTS), _, _, _, compilation_unit(UNIT)), ( SUPER_CLASS \= null; not(length(IMPLEMENTS,0)) ), model_unit(UNIT), method_declaration(METHOD, parent(CLASS), _, _, _, _, _, _, _, _, _), not(object_method(METHOD)), not(non_abstracted_method_checked(METHOD)), assert(non_abstracted_method_checked(METHOD)), not(redefine_method(METHOD,_)), findall(SIMILAR,non_abstracted_method_aux(METHOD,SIMILAR),LIST), not(length(LIST,0)), append([METHOD],LIST,ID).
no_abstracted_method_aux(METHOD,SIMILAR):- method_declaration(METHOD, parent(CLASS), NAME, _, _, parameters_types(TYPES), return_type(RETURN_TYPE), dimensions(D), _, _, _), method_declaration(SIMILAR, parent(CLASS_2), NAME, _, _, parameters_types(TYPES), return_type(RETURN_TYPE), dimensions(D), _, _, _), METHOD \= SIMILAR, not(non_abstracted_method_checked(SIMILAR)), public_method(SIMILAR), common_super(CLASS,CLASS_2,CLASS_3), CLASS_3 \= CLASS, CLASS_3 \= CLASS_2, CLASS \= CLASS_2, ( class_declaration(CLASS_3, _, _, _, _, _, _, _, _, _, _); interface_declaration(CLASS_3, _, _, _, _, _, _, _, _, _, _) ), assert(non_abstracted_method_checked(SIMILAR)).

/* Abstract class that may be an interface*/
/* This class has no fields and has no implemented methods (just abstract methods) */

abstract_class_may_be_interface(ID):-
    class_declaration(ID, _, _, _, _, super_type(null), _, _, _, _, compilation_unit(UNIT)),
    abstract_class(ID),
    not(
        (
            field_declaration(FIELD, parent(ID), _, _, _, _, _, _),
            not(static_field(FIELD))
        )
    ),
    not(
        (
            method_declaration(METHOD, parent(ID), _, _, _, _, _, _, _, _, _),
            not(abstract_method(METHOD))
        )
    ),
    model_unit(UNIT).

/* The class uses super to invoke a method */
/* If the method is inherited, it is unnecessary to use 'super' */
/* This is usually related to students missunderstanding about inheritance. The students tend to think that methods are not inherited by the child class */

super_in_method_invocation(ID):-
    super_method_invocation(ID, _, method(SUPER), arguments(ARGUMENTS), body_declaration(METHOD), _, compilation_unit(UNIT)),
    (
        method_declaration(SUPER, _, NAME, _, _, _, _, _, _, _, _);
        method(SUPER, name(NAME), _, _)
    ),
    not(method_declaration(METHOD, _, NAME, _, parameters(ARGUMENTS), _, _, _, _, _, _)),
    model_unit(UNIT).

/* The class uses super to access to field */
/* If the field is inherited, it is unnecessary to use 'super' */
/* This is usually related to students missunderstanding about inheritance. The students tend to think that fields are not inherited by the child class */

super_in_field_access(ID):-
    super_field_access(ID, _, _, _, _, compilation_unit(UNIT)),
    model_unit(UNIT).

/* Redefining a method M just invoking super.M() */
/* Redefining by just invoking super is unnecessary and redundant */
/* This is usually related to students missunderstanding about inheritance. The students tend to think that they have to redefine the inherited methods */

redefine_with_super(ID):-
    super_method_invocation(INVOCATION, parent(PARENT), method(SUPER_METHOD), _, body_declaration(ID), _, compilation_unit(UNIT)),
    method_declaration(ID, _, _, _, _, _, _, _, body(BODY), _, _),
    (
        block(BODY, _, statements([INVOCATION]), _, _, _);
        (
            block(BODY, _, statements([RETURN]), _, _, _),
            return_statement(RETURN, _, expression(INVOCATION), _, _, _)
        )
    ),
    redefine_method(ID,SUPER_METHOD),
    model_unit(UNIT).

/* Redefining a method M as abstract */
/* Redefining a method as abstract is weird and probaby unnecessary */
/* This is usually related to students missunderstanding about inheritance. */

method_redefined_as_abstract(ID):-
    method_declaration(ID, _, _, _, _, _, _, _, _, _, compilation_unit(UNIT)),
    abstract_method(ID),
    (
        object_method(ID);
        redefine_method(ID,_)
    ),
    model_unit(UNIT),
    not(method_redefined_as_abstract_checked(ID)),
    assert(method_redefined_as_abstract_checked(ID)).

/* Many interfaces implemented */
/* This occurs when a class implements too many interfaces (actually more than 2). */
/* This is not necessary an error, but the student may have assigned a lot of responsability to a single class */

many_implements(ID):-
    class_declaration(ID, _, _, _, _, _, implements(IMPLEMENTS), _, _, _, compilation_unit(UNIT)),
    length(IMPLEMENTS,LENGTH),
    LENGTH > 2, /* Maximum number of implemented interfaces */
    model_unit(UNIT).

/* Low cohesion */
/* This occurs when a class defines too many behaviors (actually more than 9) */
/* This is not necessary an error, but the student may have assigned a lot of responsability to a
single class */

low_cohesion(ID):-
    ( 
        class_declaration(ID, _, _, _, _, _, _, _, methods(METHODS), _, compilation_unit(UNIT));
        interface_declaration(ID, _, _, _, _, _, _, _, methods(METHODS), _, compilation_unit(UNIT))
    ),
    filter_methods(METHODS,JUST_METHODS),
    filter_public_methods(JUST_METHODS,PUBLIC_METHODS),
    filter_behaviours(PUBLIC_METHODS,BEHAVIOURS),
    length(BEHAVIOURS,LENGTH),
    LENGTH > 9, /* Maximum number of public behaviours */
    model_unit(UNIT).

/* Use of instanceOf */
/* This error is detected when the students uses the instanceOf to check the type of an object */
/* This is usually related with errors in the system design. Students tend to use instanceOf instead of taking advantage of the polymorphism. */
/* Note that if the instanceOf is used inside the equals method, this is not necessary an error */

use_of_instanceof(ID):-
    (
        instanceof_expression(ID, _, _, _, body_declaration(METHOD), _, compilation_unit(UNIT));
        (
            method(IS_INSTANCE, name('isInstance'), _, _),
            method_invocation(ID, _, _, method(IS_INSTANCE), arguments([]), body_declaration(METHOD), _, compilation_unit(UNIT))
        )
    ),
    not(is_equals(METHOD)),
    model_unit(UNIT).

/* Boolean field as type indicator */
/* This error is detected when the students uses a boolean field in a class to determine the behavior of the class (by checking the field's value within the methods) */
/* This is not necessary an error, but usually students use boolean fields instead of taking advantage of the polymorphism. */

boolean_as_type(ID):-
    field_declaration(FIELD, parent(CLASS), _, type(BOOLEAN), _, _, _, compilation_unit(UNIT)),
    (
        primitive_type(BOOLEAN, code('boolean'));
        type(BOOLEAN , name('Boolean'))
    ),
    model_unit(UNIT),
    findall(IF,boolean_as_type_aux(FIELD,IF),ID),
    not(length(ID,0)).

boolean_as_type_aux(FIELD,IF):-
    field_declaration(FIELD, parent(CLASS), _, type(BOOLEAN), _, _, _, _),
    if_statement(IF, _, condition(CONDITION), then(THEN), else(ELSE), _, type_declaration(CLASS), _),
    THEN \= null,
    ELSE \= null,
    (
        field_access(CONDITION,FIELD);
        (
            prefix_expression(CONDITION, _, _, operand(OPERAND), _, _, _),
            field_access(OPERAND,FIELD)
        );
        (
            (
                infix_expression(CONDITION, _, _, left_operand(OPERAND), right_operand(LITERAL), extended_operands([]), _, _, _);
                infix_expression(CONDITION, _, _, left_operand(LITERAL), right_operand(OPERAND), extended_operands([]), _, _, _)
            ),
            field_access(OPERAND,FIELD),
            boolean_literal(LITERAL, _, _, _, _, _)
        )
    ).

/* Field used in switch */ /* This is not necessary an error, but it is possible that students is using the field to determine the behavior of the object, instead of taking advantage of polymorphism */
switch_of_attribute(ID):- field_declaration(FIELD, _, _, _, _, _, _, compilation_unit(UNIT)), model_unit(UNIT), findall(SWITCH,switch_of_attribute_aux(FIELD,SWITCH),ID), not(length(ID,0)).
switch_of_attribute_aux(FIELD,SWITCH):- field_declaration(FIELD, parent(CLASS), _, _, _, _, _, _), switch_statement(SWITCH, _, switch(EXP), _, _, type_declaration(CLASS), _), field_access(EXP,FIELD).

/* Field named "type" or "tipo" */
/* This is not necessary an error, but it may be related to one */

type_attribute(ID):-
    field_declaration(ID, _, FIELD_NAME, _, _, _, _, compilation_unit(UNIT)),
    (
	contains_word(FIELD_NAME,'tipo');
	contains_word(FIELD_NAME,'type')
    ),
    model_unit(UNIT).

/* System out print */
/* The classes that model the solution to a problem should not print by console */
/* The communication with the user should be managed only by user interface classes (or by the Main class in a small project)*/

system_out_print(ID):-
    is_print(PRINT),
    method_invocation(PRINT, _, _, _, _, body_declaration(ID), _, compilation_unit(UNIT)),
    not(system_out_print_checked(ID)),
    assert(system_out_print_checked(ID)),
    model_unit(UNIT).

/* Main class as model class */
/* The main class should just instance the necessary objects and interact with user (if need). This class should not form part of a hierarchical tree */

main_class_as_model_class(ID):-
    main_class(ID),
    (
        class_declaration(ID_2, _, _, _, _, _, _, _, _, _, _);
        interface_declaration(ID_2, _, _, _, _, _, _, _, _, _, _)
    ),
    ID \= ID_2,
    common_super(ID,ID_2,_),
    not(main_class_as_model_class_checked(ID)),
    assert(main_class_as_model_class_checked(ID)).

/* Using == instead of equals */
/* This error occurs when students compare objects by using == instead of equals */
/* Using == compares memory addresses instead of object contents */

not_use_of_equals(ID):-
    infix_expression(ID, _, operator('EQUALS'), left_operand(LO), right_operand(RO), _, _, _, compilation_unit(UNIT)),
    resolve_expression(LO,LO_RESOLVED),
    resolve_expression(RO,RO_RESOLVED),
    not(null_literal(LO_RESOLVED,_,_,_,_)),
    not(null_literal(RO_RESOLVED,_,_,_,_)),
    (
        need_equals(LO_RESOLVED);
        (
            not(need_equals(LO_RESOLVED)),
            need_equals(RO_RESOLVED)
        )
    ),
    model_unit(UNIT).

/* Method always return null */
/* This may be related to an error. */

method_always_returns_null(ID):-
    method_declaration(ID, _, _, _, _, _, return_type(TYPE), _, body(B), _, compilation_unit(UNIT)),
    B \= null,
    not(primitive_type(TYPE, code('void'))),
    not(
        (
            return_statement(_, _, expression(LITERAL), body_declaration(ID), _, _),
            not(null_literal(LITERAL, _, _, _, _))
        )
    ),
    model_unit(UNIT).

/* Constant in a method invocation */
/* This may be related to an error. Maybe the student should replace the constant with a variable (or with a final field if it is a constant) in order to have a more flexible code */

constant_in_invocation(LITERAL):-
    (
        boolean_literal(LITERAL,parent(INVOCATION),value(VAL),_,_,compilation_unit(UNIT));
        character_literal(LITERAL,parent(INVOCATION),value(VAL),_,_,compilation_unit(UNIT));
        number_literal(LITERAL,parent(INVOCATION),value(VAL),_,_,compilation_unit(UNIT));
        string_literal(LITERAL,parent(INVOCATION),value(VAL),_,_,compilation_unit(UNIT))
    ),
    not(valid_literal(VAL)),
    (
        (
            method_invocation(INVOCATION, _, _, _, _, _, _, _),
            not(is_print(INVOCATION))
        );
        constructor_invocation(INVOCATION, _, _, _, _, _, _);
        class_instance_creation(INVOCATION, _, _, _, _, _, _, _);
        super_method_invocation(INVOCATION, _, _, _, _, _, _);
        super_constructor_invocation(INVOCATION, _, _, _, _, _, _, _)
    ),
    not(dinamic_attribute(LITERAL)),
    model_unit(UNIT).

